import{_ as a,o as t,c as r,am as p}from"./chunks/framework.DLqCyLYu.js";const m=JSON.parse('{"title":"如何制作HotPE模块","description":"","frontmatter":{},"headers":[],"relativePath":"devdoc/makehpm.md","filePath":"devdoc/makehpm.md","lastUpdated":1771157741000}'),o={name:"devdoc/makehpm.md"};function s(i,e,h,n,l,c){return t(),r("div",null,[...e[0]||(e[0]=[p('<h1 id="如何制作hotpe模块" tabindex="-1">如何制作HotPE模块 <a class="header-anchor" href="#如何制作hotpe模块" aria-label="Permalink to “如何制作HotPE模块”">​</a></h1><h2 id="获取模块制作工具" tabindex="-1">获取模块制作工具 <a class="header-anchor" href="#获取模块制作工具" aria-label="Permalink to “获取模块制作工具”">​</a></h2><ul><li>下载独立的HotPE模块制作工具，链接：<a href="https://p0.hotpe.top/HotPE%E6%A8%A1%E5%9D%97/HPMMake.exe" target="_blank" rel="noreferrer">https://p0.hotpe.top/HotPE模块/HPMMake.exe</a></li></ul><h2 id="处理绿色软件" tabindex="-1">处理绿色软件 <a class="header-anchor" href="#处理绿色软件" aria-label="Permalink to “处理绿色软件”">​</a></h2><p>找到软件的目录和可执行文件,以GitHubDesktop为例</p><p><img src="https://sc.sysri.cn/i/2022/05/02/10gzmro.png" alt=""></p><p>进入HotPE运行程序可执行文件，如果不可以正常运行就排查问题（添加dll文件、运行库等），处理完成之后回到本地系统</p><h2 id="打包模块" tabindex="-1">打包模块 <a class="header-anchor" href="#打包模块" aria-label="Permalink to “打包模块”">​</a></h2><p>打开HotPE模块制作工具，模块名称填软件名称，描述填软件介绍 ...，参数不要含空格，点击“下一步”</p><p><img src="https://sc.sysri.cn/i/2022/05/02/10i48f8.png" alt=""></p><p>嵌入目录选择程序目录（单文件程序请确保不含其它文件），主程序请选择在嵌入目录内的可执行文件，点“下一步”</p><p><img src="https://sc.sysri.cn/i/2022/05/02/10ins4i.png" alt=""></p><p>在模块制作前你还可以通过编辑模块嵌入文件和脚本实现更多自定义功能。点击“开始制作”制作模块</p><p><img src="https://sc.sysri.cn/i/2022/05/02/10iuqdy.png" alt=""></p><p>制作完成后请在HotPE中测试，没有问题后就可以 <a href="https://docs.hotpe.top/devdoc/hpm_con.html" target="_blank" rel="noreferrer">投稿模块</a> 啦</p>',15)])])}const _=a(o,[["render",s]]);export{m as __pageData,_ as default};
